mapper.select:
SELECT m.document_id AS documentId, m.title AS title, t.type_name AS typeName, m.achievement_status AS auditStatus, m.authors AS author, m.project_name AS projectName, m.summary AS summary, m.created_at AS createdAt, m.visibility_range AS visibilityRange FROM achievement_mains m
LEFT JOIN achievement_mains_achievement_type_id_lnk mt ON mt.achievement_main_id = m.id LEFT JOIN achievement_types t ON t.id = mt.achievement_type_id LEFT JOIN admin_users au ON au.id = m.created_by_id
WHERE m.is_delete = 0 AND m.published_at IS NOT NULL
AND m.title LIKE CONCAT('%', #{dto.mainTitle}, '%')
AND m.achievement_status = #{dto.status}
AND m.reviewer_id IS NULL
AND mt.achievement_type_id = #{dto.typeId}
AND t.type_code = #{dto.typeCode}
AND m.visibility_range = #{dto.visibilityRange}
ORDER BY m.created_at DESC

SELECT t.type_name AS typeName, t.type_code AS typeCode, COUNT(DISTINCT m.id) AS count FROM achievement_types t LEFT JOIN achievement_mains_achievement_type_id_lnk mt ON mt.achievement_type_id = t.id LEFT JOIN achievement_mains m ON m.id = mt.achievement_main_id AND m.is_delete = 0 AND m.published_at IS NOT NULL AND m.achievement_status = 'APPROVED'
AND m.created_by_id = #{creatorId}
WHERE t.is_delete = 0 GROUP BY t.id, t.type_name, t.type_code ORDER BY count DESC

SELECT COUNT(1)
FROM achievement_mains m
WHERE m.is_delete = 0
  AND m.published_at IS NOT NULL
  AND m.achievement_status = 'APPROVED'
  AND m.created_by_id = #{userId}
  AND m.created_at  >=  #{start}
  AND m.created_at  <  #{end}
