-- ----------------------------
-- Procedure structure for safe_add_column
-- ----------------------------
DROP PROCEDURE IF EXISTS `safe_add_column`;
delimiter ;;
CREATE PROCEDURE `safe_add_column`(IN p_table_name VARCHAR(64),
    IN p_column_name VARCHAR(64),
    IN p_column_definition TEXT)
BEGIN
    DECLARE column_exists INT DEFAULT 0;
    
    -- 检查列是否存在
    SELECT COUNT(*) INTO column_exists
    FROM INFORMATION_SCHEMA.COLUMNS
    WHERE TABLE_SCHEMA = DATABASE()
      AND TABLE_NAME = p_table_name
      AND COLUMN_NAME = p_column_name;
    
    -- 如果不存在则添加
    IF column_exists = 0 THEN
        SET @sql = CONCAT('ALTER TABLE `', p_table_name, '` ADD COLUMN `', p_column_name, '` ', p_column_definition);
        PREPARE stmt FROM @sql;
        EXECUTE stmt;
        DEALLOCATE PREPARE stmt;
        SELECT CONCAT('✅ 已添加字段: ', p_table_name, '.', p_column_name) AS message;
    ELSE
        SELECT CONCAT('⚠️ 字段已存在，跳过: ', p_table_name, '.', p_column_name) AS message;
    END IF;
END
;;
delimiter ;

