DROP PROCEDURE IF EXISTS `safe_add_index`;
delimiter ;;
CREATE PROCEDURE `safe_add_index`(IN p_table_name VARCHAR(64),
    IN p_index_name VARCHAR(64),
    IN p_index_definition TEXT)
BEGIN
    DECLARE index_exists INT DEFAULT 0;
    
    -- 检查索引是否存在
    SELECT COUNT(*) INTO index_exists
    FROM INFORMATION_SCHEMA.STATISTICS
    WHERE TABLE_SCHEMA = DATABASE()
      AND TABLE_NAME = p_table_name
      AND INDEX_NAME = p_index_name;
    
    -- 如果不存在则添加
    IF index_exists = 0 THEN
        SET @sql = CONCAT('ALTER TABLE `', p_table_name, '` ADD INDEX `', p_index_name, '` ', p_index_definition);
        PREPARE stmt FROM @sql;
        EXECUTE stmt;
        DEALLOCATE PREPARE stmt;
        SELECT CONCAT('✅ 已添加索引: ', p_table_name, '.', p_index_name) AS message;
    ELSE
        SELECT CONCAT('⚠️ 索引已存在，跳过: ', p_table_name, '.', p_index_name) AS message;
    END IF;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for safe_create_table
-- ----------------------------
DROP PROCEDURE IF EXISTS `safe_create_table`;
delimiter ;;
CREATE PROCEDURE `safe_create_table`(IN p_table_name VARCHAR(64),
    IN p_table_definition TEXT)
BEGIN
    DECLARE table_exists INT DEFAULT 0;
    
    -- 检查表是否存在
    SELECT COUNT(*) INTO table_exists
    FROM INFORMATION_SCHEMA.TABLES
    WHERE TABLE_SCHEMA = DATABASE()
      AND TABLE_NAME = p_table_name;
    
    -- 如果不存在则创建
    IF table_exists = 0 THEN
        SET @sql = p_table_definition;
        PREPARE stmt FROM @sql;
        EXECUTE stmt;
        DEALLOCATE PREPARE stmt;
        SELECT CONCAT('✅ 已创建表: ', p_table_name) AS message;
    ELSE
        SELECT CONCAT('⚠️ 表已存在，跳过: ', p_table_name) AS message;
    END IF;
END
;;
delimiter ;

